import React, { Component } from 'react'
import axios from 'axios'
import { movies } from './Getmovies';
export default class Movies extends Component 
{
    constructor()
    {
        super();
        this.state=
        {
            hover:'',
            parr:[1],
            currPage:1,
            movies:[],
            favourites:[]
        }
    }
    async componentDidMount()
    {
        const res = await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=5540e483a20e0b20354dabc2d66a31c9&language=en-US&page=${this.state.currPage}`);
        let data = res.data

        this.setState({
            movies:[...data.results]
        })
    }
 changeMovies=async()=>
    {
        const res = await axios.get(`https://api.themoviedb.org/3/movie/popular?api_key=5540e483a20e0b20354dabc2d66a31c9&language=en-US&page=${this.state.currPage}`);
        let data = res.data

        this.setState({
            movies:[...data.results]
        })
    }
    handleRight=()=>
    {
        let temparr=[]
        for(let i=1;i<=this.state.parr.length+1;i++)
        {
           temparr.push(i);
        }
        this.setState(
            {
                parr:[...temparr],
                currPage:this.state.currPage+1
            },this.changeMovies)

    }
    handleLeft=()=>
    {
       if(this.state.currPage>1)
       {
        this.setState(
            {
                currPage:this.state.currPage-1

            }
            ,this.changeMovies)}

    }
    handleclick=(value)=>
    {
     if(value!=this.state.currPage)
     {
        this.setState(
            {
                currPage:value,
            },this.changeMovies)
        }

    }
    handleFavourites=(movie)=>
    {
        let oldData = JSON.parse(localStorage.getItem("movies-app") || "[]")
        if(this.state.favourites.includes(movie.id)){
            oldData = oldData.filter((m)=>m.id!=movie.id)
        }else{
            oldData.push(movie)
        }
        localStorage.setItem("movies-app",JSON.stringify(oldData));
        console.log(oldData);
       this.hand

    }
    handleFavouritesstate=()=>
    {
        let oldData = JSON.parse(localStorage.getItem("movies-app") || "[]")
          let temp=oldData.map((movie)=>movie.id)
        this.setState({
          favourites:[...temp]
    })


    }

  render() 
  {
    return (
     <>
     {
         this.state.movies.length==0?
         <div class="spinner-border text-primary" role="status">
                        <span class="visually-hidden">Loading...</span>
                    </div>:
         <div>
           <h3 className="text-center"><strong>Trending</strong></h3>
           <div className='movies-list'>
            {

            this.state.movies.map((movieobj)=>(
            <div className="card movies-card" onMouseEnter={()=>this.setState({hover:movieobj.id})}>
            <img src={`https://image.tmdb.org/t/p/original${movieobj.backdrop_path}`} alt={movieobj.title} className="card-img-top movies-img"/>
            {/* <div className="card-body"> */}
            <h5 className="card-title movies-title">{movieobj.original_title}</h5>
            {/* <p className="card-text movies-text">{movieobj.overview}</p> */}
            <div className="button-wrapper" style={{display:"flex",justifyContent:"center",width:"100%"}}>
            {
                this.state.hover==movieobj.id&& 
                <a className="btn btn-primary movies-button" onClick={()=>this.handleFavourites(movieobj)}>Add to Favourites</a>    
            }    

            </div>
            </div>
                ))
            }
           </div>
           <div style={{display:'flex',justifyContent:'center'}}>
            <nav aria-label="Page navigation example">
            <ul class="pagination">
            <li class="page-item"><a class="page-link" onClick={this.handleLeft}>Previous</a></li>  


                {
                    this.state.parr.map((value)=>(
                        <li class="page-item"><a class="page-link"onClick={()=>this.handleclick(value)} >{value}</a></li>  
                    ))
                }


            <li class="page-item"><a class="page-link" onClick={this.handleRight}>Next</a></li>
            </ul>
            </nav>
            </div>
            </div>

     }
     </>
    )
  }
}
