import React, { Component } from 'react'
import { movies } from './Getmovies'
export default class Favourities extends Component {
  constructor()
  {
    super();
    this.state={
      curgen:"All Generes",
      movie:[],
      generes:[]

    }
  }
  componentDidMount()
  {
    let genreids = {28:'Action',12:'Adventure',16:'Animation',35:'Comedy',80:'Crime',99:'Documentary',18:'Drama',10751:'Family',14:'Fantasy',36:'History',
    27:'Horror',10402:'Music',9648:'Mystery',10749:'Romance',878:'Sci-Fi',10770:'TV',53:'Thriller',10752:'War',37:'Western'};
    let data=JSON.parse(localStorage.getItem("movies-app")||"[]")
    let temp=[];
    temp.push("All Generes")
  data.map((movieObj)=>{
        if(!temp.includes(genreids[movieObj.genre_ids[0]]))
        {
          temp.push(genreids[movieObj.genre_ids[0]])
        }
    })
    this.setState({
      generes:[...temp],
      movi
    })
  }
  render() 
  {

    return (
      <div>
        <>

<div className="main">
    <div className="row">
        <div className="col-3">
           <ul class="list-group favourities-generes">
                  {
                    temp.map((tempObj)=>(
                      this.state.curgen==tempObj?
                      <li class="list-group-item" style={{background:'#3f51b5',color:"white"}}>{tempObj}</li>:
                      <li class="list-group-item" style={{background:'white',color:"#3f51b5"}}>{tempObj}</li>

                    ))

                  }


            </ul>
        </div> 

            <div className="col-9">
               <div className="row favourities-table">
                  <input type="text" className="input-group-text col" placeholder="Search"/>
                  <input type="number" className="input-group-text col" placeholder="Rows count"/>
                    <table class="table">
                            <thead>
                                <tr>
                                <th scope="col">title</th>
                                <th scope="col">Genere</th>
                                <th scope="col">Popularity</th>
                                <th scope="col">Rating</th>
                                </tr>
                            </thead>
                            <tbody>

                               {
                                movie.map((moviesObj)=>(
                                    <tr>
                                    <td>{<img src={`https://image.tmdb.org/t/p/original${moviesObj.backdrop_path}`} alt={moviesObj.title} style={{width:'5rem'}}/>}{" "}{ moviesObj.title}</td>
                                    <td>{genreids[moviesObj.genre_ids[0]]}</td>
                                    <td>{moviesObj.popularity}</td>
                                    <td>{moviesObj.vote_average}</td>
                                    <td><button type="button" class="btn btn-danger">Remove</button></td>
                                    </tr>
                                ))}
                            </tbody>
                    </table>
                    <nav aria-label="Page navigation example">
                        <ul class="pagination">

                            <li class="page-item"><a class="page-link" href="#">1</a></li>
                            <li class="page-item"><a class="page-link" href="#">2</a></li>
                            <li class="page-item"><a class="page-link" href="#">3</a></li>

                        </ul>
                    </nav>
               </div> 
            </div> 
    </div>
</div>


      </>
      </div>
    )
  }
}
